import React from 'react';
import '../styles/Menu.css';

const Menu = ({ active }) => {
  if(active) {
    return (
      <div className={menu}>
        Press <span>enter</span> to start<br />
        <span>w a s d</span> keys to control
      </div>
    );
  }

  else {
    return (
      <div></div>
    );
  }
}

export default Menu;
